// @ts-ignore
import { FiPlusSquare } from 'react-icons/fi';
import './App.css';

import './style.css';
import { useDispatch, useSelector } from '../node_modules/react-redux/dist/react-redux';
import Input from './components/input';
import Button from './components/button';
import { useState } from 'react';
import { addNote } from './redux/reducer';


function App() {
  const [text,setText]=useState(' ')
  const notes=useSelector(notes=>notes.note)
  const handleChange=(value)=>{
    setText(value)
  }

 const dispatch=useDispatch()
 const handleClick=()=> {

  dispatch(addNote())
  setText("")
 }
 console.log(notes)
  return (
    <div className="App">
      <div className='form-container'>
       <Input  placeholder="Add Note" value={text} handleChange={handleChange} />
       <Button value="Add Note" type="button" handleClick={handleClick}/>

     </div>
     <h3>Notes</h3>
       <span><FiPlusSquare className='icons'/></span>
       {text}
    </div>
  );
}

export default App;
