// @ts-ignore
import { createSlice } from "@reduxjs/toolkit";
const initialState = [];

const addNoteReducer = createSlice({
  name: "note",
  initialState,
  reducers: {
    addNote: (state, action) => {
      state.push(action.payload);
      return state;
    },
    deleteNote:(state,action)=>{
      const index = state.findIndex((todo,i) => i === action.payload);
      console.log(index)
      if (index !== -1) {
        state.splice(index, 1);

      }

    }
  },
});

export const {addNote,deleteNote}=addNoteReducer.actions;
export const reducer=addNoteReducer.reducer
