// @ts-ignore
import { createSlice } from "@reduxjs/toolkit";
const initialState = [];

const addNoteReducer = createSlice({
  name: "note",
  initialState,
  reducers: {
    addNote: (state, action) => {
      state.push(action.payload);
      return state;
    },
    deleteNote:(state,action)=>{
      console.log("states",initialState,action)
      return state
    }
  },
});

export const {addNote,deleteNote}=addNoteReducer.actions;
export const reducer=addNoteReducer.reducer
