// @ts-nocheck
// @ts-ignore
import { FiPlusSquare } from "react-icons/fi";
import "./App.css";

import { useState } from "react";
import {
  useDispatch,
  useSelector,
} from "../node_modules/react-redux/dist/react-redux";
import Button from "./components/button";
import Input from "./components/input";
import { addNote, deleteNote, editNote } from "./redux/reducer";
import "./style.css";

function App() {
  const [text, setText] = useState(" ");
  const notes = useSelector((notes) => notes.noteList);
  const handleChange = (value) => {
    setText(value);
  };

  const dispatch = useDispatch();
  const handleClick = () => {
    dispatch(addNote(text));
    setText("");
  };
  const handleDeleteNote = (id) => dispatch(deleteNote(id));
  const handleEditNote = (id) => {
    dispatch(editNote(id));
  };

  return (
    <div className="App">
      <div className="form-container">
        <Input
          placeholder="Add Note"
          value={text}
          handleChange={handleChange}
        />
        <Button value="Add Note" type="button" handleClick={handleClick} />
      </div>
      <div>
        <h3>Notes</h3>

        <ul>
          {notes.map((item, i) => (
            <li key={i}>
              <Input value={item} handleChange={handleChange} />
              <span>
                <FiPlusSquare
                  className="icons"
                  onClick={() => handleDeleteNote(i + 1)}
                />
                <FiEdit />
              </span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
